import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--darkGrey);
  border-radius: 20px;
  color: var(--white);
  padding: 5px;
  text-align: center;
  h3 {
    margin: 15px 0 0 0;
  }
  p {
    margin: 5px 0;
  }

  :hover {
    filter: brightness(75%);
  }
`;

export const Image = styled.img`
  border-radius: 15px;
  display: block;
  height: 200px;
  width: 100%;
  max-width: 720px;
  object-fit: cover;
`;

export const NoImage = styled.div`
  align-items: center;
  border-radius: 15px;
  color: var(--white);
  display: flex;
  font-size: var(--fontBig);
  height: 200px;
  justify-content: center;
  width: 100%;
  max-width: 720px;
`;
