// API
import API from "../API";
// Hook
import { useState, useEffect } from "react";

export const usePersonCreditsFetch = (personId) => {
  const [state, setState] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  console.log("not fetching personcredits yet");
  useEffect(() => {
    const fetchData = async () => {
      try {
        setError(false);
        console.log("error false");
        setLoading(true);
        console.log("load true");
        const credits = await API.fetchPersonCredits(personId);
        const actor = credits.cast;
        const director = credits.crew.filter(
          (movie) => movie.job === "Director"
        );
        const producer = credits.crew.filter(
          (movie) => movie.job === "Producer"
        );
        const writer = credits.crew.filter((movie) => movie.job === "Writer");
        setState({ actor, director, producer, writer });
      } catch {
        console.log("error");
        setError(true);
      }
      console.log("load false");
      setLoading(false);
    };
    fetchData();
  }, [personId]);
  return { state, error, loading };
};
