import styled from "styled-components";

export const Wrapper = styled.div`
  /* width: 100%; */
  display: flex;
  align-items: center;
  height: 100px;
  padding: 0 20px;
  background-color: cyan;
`;

export const Content = styled.div`
  background: var(--medGrey);
  height: 55px;
  width: 100%;
  margin: 0 auto;
  color: var(--white);
  border-radius: 25px;

  input {
    border-radius: 15px;
    width: 80%;
  }
`;
