import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--medGrey);
  height: 100%;
  min-height: 100vh;
  width: 100%;
  min-width: 100vw;
  padding: 25px 50px;
  /* background: cyan; */
`;

export const Content = styled.div`
  height: 100%;
  margin: 0 auto;
  /* background: green; */
  color: var(--white);
  display: grid;
  justify-content: center;
  width: 1000px;
  grid-template-columns: 800px 200px;
`;
