import styled from "styled-components";

export const Wrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 175px);
  grid-gap: 20px;
  height: 50%;
`;

export const Image = styled.img`
  border-radius: 5px;
  object-fit: cover;
  width: 100%;
  height: 100%;
`;
