import React from "react";
// Component
import ReadMore from "../ReadMore";
// Config
import { IMAGE_BASE_URL, PROFILE_SIZE } from "../../config";
// Styled
import { Wrapper, Image } from "./Bio.styles";
import no_image from "../../images/no_image.jpg";

export const Bio = ({ person, loading }) => {
  return (
    <Wrapper>
      {!loading && person.profile_path ? (
        <Image
          src={`${IMAGE_BASE_URL}${PROFILE_SIZE}${person.profile_path}`}
          alt="profile-thumb"
        />
      ) : (
        <Image src={no_image} />
      )}
      {!loading && <ReadMore biography={person.biography} />}
    </Wrapper>
  );
};

export default Bio;
