import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  align-items: center;
  height: 100px;
  padding: 0 20px;
`;

export const Content = styled.div`
  background: var(--medGrey);
  border-radius: 50px;
  color: var(--white);
  height: 55px;
  margin: 0 auto;
  width: 100%;
  max-width: var(--maxWidth);
  position: relative;

  img {
    position: absolute;
    top: 14px;
    left: 15px;
    width: 30px;
  }

  input {
    background: transparent;
    border: none;
    border-radius: 15px;
    color: var(--white);
    font-size: 28px;
    height: 40px;
    width: 97.5%;
    margin: 8px 0;
    padding: 0 0 0 60px;
    position: absolute;
    left: 0;
  }
`;
