import React from "react";
// Component
import CreditsGrid from "../CreditsGrid";
import { Wrapper } from "./Credits.style";
import { usePersonCreditsFetch } from "../../hooks/usePersonCreditsFetch";
import { useState } from "react/cjs/react.development";

const Credits = ({ personId }) => {
  const { state: credits, error, loading } = usePersonCreditsFetch(personId);
  const [showing, setShowing] = useState("director");
  let keys;
  if (!loading && credits) {
    keys = Object.keys(credits);
    // setShowing(keys);
    // console.log(showing);
  }

  return (
    <>
      {keys.map((key) => (
        <select value={key}></select>
      ))}
      <Wrapper>{/* <CreditsGrid showing={credits[showing[0]]} /> */}</Wrapper>
    </>
  );
};

export default Credits;
