import React from "react";
// Component
import CreditsGrid from "../CreditsGrid";
import { Wrapper } from "./Credits.style";
import { usePersonCreditsFetch } from "../../hooks/usePersonCreditsFetch";
import { useState } from "react/cjs/react.development";
import Dropdown from "../Dropdown";

const Credits = ({ personId }) => {
  const { state: credits, error, loading } = usePersonCreditsFetch(personId);
  const [showing, setShowing] = useState("");
  let jobs;
  if (!loading && credits) {
    console.log(credits);
    keys = Object.keys(credits);
    jobs = keys.map((key) => credits[key].length > 0);
  }
  const handleSelect = (job) => {
    setShowing(job);
    console.log(showing);
  };
  return (
    <>
      <Wrapper>
        {!loading && (
          <Dropdown
            credits={credits}
            jobs={keys}
            handleSelect={handleSelect}
          ></Dropdown>
        )}
        {/* <CreditsGrid showing={credits[showing[0]]} /> */}
      </Wrapper>
    </>
  );
};

export default Credits;
