import React, { useEffect } from "react";
// Component
import CreditsGrid from "../CreditsGrid";
import { Content, Wrapper } from "./Credits.style";
import { usePersonCreditsFetch } from "../../hooks/usePersonCreditsFetch";
import { useState } from "react/cjs/react.development";
import Dropdown from "../Dropdown";
import Spinner from "../Spinner";

const Credits = ({ person, personId }) => {
  const { state: credits, loading } = usePersonCreditsFetch(personId);
  const [showing, setShowing] = useState();

  let keys = Object.keys(credits);
  // Only show select options for previously held jobs
  let jobs = keys.filter((key) => credits[key].length > 0);
  // Sort jobs by the most number of credits
  jobs = jobs.sort((a, b) => credits[b].length - credits[a].length);

  const handleSelect = (job) => {
    setShowing(job);
  };

  // useEffect(() => {
  //   console.log("dropdown useeffect");
  //   handleSelect(jobs[0]);
  // }, []);

  if (loading || !Object.keys(credits).length) {
    console.log("here");
    return <Spinner />;
  }

  return (
    <Wrapper>
      <Content>
        <div className="job-type">
          <div>
            FILMS{" "}
            {showing === "actor"
              ? "STARRING"
              : showing === "director"
              ? "DIRECTED BY"
              : showing === "producer"
              ? "PRODUCED BY"
              : "WRITTEN BY"}
          </div>
          <div className="name">{person.name}</div>
        </div>
        <Dropdown credits={credits} jobs={jobs} handleSelect={handleSelect} />
        <CreditsGrid showing={credits[showing]} />
      </Content>
    </Wrapper>
  );
};

export default Credits;
