import React from "react";
// Component
import NotFound from "../NotFound";
// Config
import { IMAGE_BASE_URL, PROFILE_SIZE } from "../../config";
// Hook
import { usePersonFetch } from "../../hooks/usePersonFetch";
// Styled
import { Wrapper, Image, Text } from "./Bio.styles";

export const Bio = ({ personId }) => {
  const { state: person, loading, error } = usePersonFetch(personId);
  console.log(person);
  return (
    <Wrapper>
      <div className="profile-image">
        {person.profile_path ? (
          <Image
            src={`${IMAGE_BASE_URL}${PROFILE_SIZE}${person.profile_path}`}
          />
        ) : (
          <NotFound />
        )}
      </div>
      <Text>{person.biography}</Text>
    </Wrapper>
  );
};

export default Bio;
