import React from "react";

// Component
import Bio from "../Bio";
import Credits from "../Credits";
import { Wrapper, Content } from "./Person.styles";
// Hook
import { usePersonFetch } from "../../hooks/usePersonFetch";
// Router
import { useParams } from "react-router";
import Spinner from "../Spinner";

const Person = () => {
  const { personId } = useParams();
  const { state: person, loading } = usePersonFetch(personId);
  if (loading) {
    console.log(loading, "person loading");
    return <Spinner />;
  }
  console.log("person");
  return (
    <Wrapper>
      <Content>
        <Credits person={person} />
        <Bio person={person} loading={loading} />
      </Content>
    </Wrapper>
  );
};

export default Person;
