import styled from "styled-components";

export const Wrapper = styled.div`
  background: blue;
  display: grid;
  width: 100%;
  grid-template-columns: repeat(4, 1fr);
  object-fit: cover;
  /* grid-gap: 10px; */
  /* justify-content: center; */
`;

export const Image = styled.img`
  /* width: 150px; */
  border-radius: 5px;
`;
