import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--medGrey);
  height: 100vh;
  width: 100vw;
  padding: 25px 50px;
  /* background: cyan; */
`;

export const Content = styled.div`
  margin: 0 auto;
  background: green;
  color: var(--white);
  display: grid;
  justify-content: center;
  width: 1000x;
  grid-template-columns: 800px 200px;
  /* grid-template-rows: 1fr; */
`;
