import styled from "styled-components";

export const Wrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 175px);
  grid-gap: 20px;
`;

export const Image = styled.img`
  border: 2.5px solid transparent;
  border-radius: 5px;
  height: 100%;
  width: 100%;
  object-fit: cover;

  :hover {
    border: 2.5px solid green;
  }
`;
