import styled from "styled-components";

export const Image = styled.img`
  border-radius: 20px;
  max-width: 720px;
  display: flex;
  object-fit: cover;
  width: 100%;
  animation: animateThumb 0.5s;

  :hover {
    cursor: pointer;
    filter: brightness(0.8);
  }

  @keyframes animateThumb {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
`;
