import styled from "styled-components";

export const Wrapper = styled.div`
  /* max-width: 150px; */
  background: linear-gradient(
      to bottom,
      rgba(0, 0, 0, 0) 41%,
      rgba(0, 0, 0, 0.65) 100%
    ),
    url(${(props) => props.image}, var(--darkGrey));
`;

export const Content = styled.div``;

export const Text = styled.div``;
