import React from "react";

const Dropdown = ({ credits, jobs, handleSelect }) => {
  return (
    <select>
      {jobs.map((job) => (
        <option
          value={job}
          onClick={(e) => handleSelect(e.currentTarget.value)}
        >
          {job.toUpperCase()} ({credits[job].length})
        </option>
      ))}
    </select>
  );
};

export default Dropdown;
