import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--medGrey);
  height: 100%;
  min-height: 100vh;
  width: 100%;
  min-width: 1000px;
  padding: 25px 50px;
`;

export const Content = styled.div`
  color: var(--white);
  display: grid;
  height: 100%;
  grid-template-columns: 800px 200px;
  justify-content: center;
`;
