import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--medGrey);
  height: 100%;
  min-height: 100vh;
  width: 100%;
  /* min-width: 1000px; */
  padding: 25px 50px;
`;

export const Content = styled.div`
  color: var(--white);
  display: grid;
  grid-template-columns: [credits] minmax(auto, 800px) [bio] 200px;
  grid-column-gap: 10px;
  height: 100%;
  justify-content: center;

  @media screen and (max-width: 768px) {
    grid-template-columns: [bio] 200px [credits] minmax(auto, 800px);
  }
`;
