import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--darkGrey);
  border-radius: 20px;
  color: var(--white);
  padding: 5px;
  text-align: center;
`;

export const Image = styled.img`
  border-radius: 15px;
  display: block;
  height: 200px;
  width: 100%;
  max-width: 720px;
  object-fit: cover;
`;
