import React from "react";
// Component
import CreditsGrid from "../CreditsGrid";
import { Content, Wrapper } from "./Credits.style";
import { usePersonCreditsFetch } from "../../hooks/usePersonCreditsFetch";
import { useState } from "react/cjs/react.development";
import Dropdown from "../Dropdown";

const Credits = ({ person, personId }) => {
  const { state: credits, error, loading } = usePersonCreditsFetch(personId);
  const [showing, setShowing] = useState();
  let jobs;

  if (!loading && credits) {
    let keys = Object.keys(credits);
    // Only show select options for previously held jobs
    jobs = keys.filter((key) => credits[key].length > 0);
  }

  const handleSelect = (job) => {
    setShowing(job);
    console.log(showing);
  };

  return (
    <>
      <Wrapper>
        <Content>
          <div className="credit-description">
            <div>
              Films{" "}
              {showing === "actor"
                ? "starring"
                : showing === "director"
                ? "directed by"
                : showing === " producer"
                ? "produced by"
                : "written by"}
            </div>
            <h3>{person.id}</h3>
          </div>
          {!loading && (
            <Dropdown
              credits={credits}
              jobs={jobs}
              handleSelect={handleSelect}
            ></Dropdown>
          )}
          {showing && <CreditsGrid showing={credits[showing]} />}
        </Content>
      </Wrapper>
    </>
  );
};

export default Credits;
