import styled from "styled-components";

export const Wrapper = styled.div`
  /* width: 100%; */
  display: flex;
  align-items: center;
  height: 100px;
  padding: 0 20px;
  background-color: cyan;
`;

export const Content = styled.div`
  background: var(--medGrey);
  height: 55px;
  width: 100%;
  margin: 0 auto;
  color: var(--white);
  border-radius: 50px;
  position: relative;

  img {
    position: absolute;
    top: 14px;
    left: 15px;
    width: 30px;
  }

  input {
    width: 95%;
    height: 40px;
    color: var(--white)
    border-radius: 15px;
    font-size: 28px;
    position: absolute;
    left: 0;
    margin: 8px 0;
    padding: 0 0 0 60px;
    border: 0;
  }
`;
