import React from "react";

// Component
import Bio from "../Bio";
import Credits from "../Credits";
import { Wrapper, Content } from "./Person.styles";
// Hook
import { usePersonFetch } from "../../hooks/usePersonFetch";
// Router
import { useParams } from "react-router";

const Person = () => {
  const { personId } = useParams();
  const { state: person, loading, error } = usePersonFetch(personId);
  console.log(person);
  return (
    <Wrapper>
      <Content>
        <Credits personId={person.id} />
        <Bio person={person} loading={loading} />
      </Content>
    </Wrapper>
  );
};

export default Person;
