import React, { useEffect } from "react";
// Component
import { Wrapper } from "./ReadMore.styles";
// Hook
import { useState } from "react";

const ReadMore = ({ biography }) => {
  const [text, setText] = useState("");
  if (biography.length > 280) {
    // console.log(biography.slice(0, 280));
    const textPortion = biography.slice(0, 280);
    setText(textPortion);
  } else setText(biography);

  return (
    <Wrapper>
      <div>{text}</div>
    </Wrapper>
  );
};

export default ReadMore;
