import styled from "styled-components";

export const Wrapper = styled.div`
  background: var(--medGrey);
  padding: 25px;
`;

export const Content = styled.div`
  color: var(--white);
  display: grid;
  height: 100vh;
  grid-template-columns: auto 2fr;
  grid-template-rows: 1fr;
`;
