import React from "react";
// Component
import ReadMore from "../ReadMore";
// Config
import { IMAGE_BASE_URL, PROFILE_SIZE } from "../../config";
// Hook
import { usePersonFetch } from "../../hooks/usePersonFetch";
// Styled
import { Wrapper, Image, Text } from "./Bio.styles";
import no_image from "../../images/no_image.jpg";

export const Bio = ({ personId }) => {
  const { state: person, loading, error } = usePersonFetch(personId);
  console.log(person);
  return (
    <Wrapper>
      {person.profile_path ? (
        <Image src={`${IMAGE_BASE_URL}${PROFILE_SIZE}${person.profile_path}`} />
      ) : (
        <Image src={no_image} />
      )}
      <ReadMore>{person.biography}</ReadMore>
    </Wrapper>
  );
};

export default Bio;
