import React from "react";
import { Wrapper, Image } from "./CreditsGrid.styles";
// Config
import { IMAGE_BASE_URL, PROFILE_SIZE } from "../../config";
// Router
import { Link } from "react-router-dom";
// Styled
import no_image from "../../images/no_image.jpg";

const CreditsGrid = ({ showing }) => {
  console.log(showing);
  return (
    <Wrapper>
      {showing.map((movie) => (
        <Link key={movie.id} to={`/${movie.id}`}>
          {movie.poster_path && (
            <Image
              src={`${IMAGE_BASE_URL}${PROFILE_SIZE}${movie.poster_path}`}
              title={movie.title}
            />
          )}

          {/* <Image
            src={
              movie.poster_path
                ? `${IMAGE_BASE_URL}${PROFILE_SIZE}${movie.poster_path}`
                : no_image
            }
          /> */}
        </Link>
      ))}
    </Wrapper>
  );
};

export default CreditsGrid;
