import React from "react";
import { Wrapper, Image, NoImage } from "./CreditsGrid.styles";
// Config
import { IMAGE_BASE_URL, PROFILE_SIZE } from "../../config";
// Router
import { Link } from "react-router-dom";
// Styled
import no_image from "../../images/no_image.jpg";

const CreditsGrid = ({ showing }) => {
  return (
    <Wrapper>
      {showing.map((movie) => (
        <Link
          key={movie.id}
          to={`/${movie.id}`}
          style={{ textDecoration: "none" }}
        >
          {movie.poster_path && (
            <Image
              src={`${IMAGE_BASE_URL}${PROFILE_SIZE}${movie.poster_path}`}
              title={movie.title}
            />
          )}
          {!movie.poster_path && <NoImage>{movie.title}</NoImage>}
          {/* <Image
            src={
              movie.poster_path
                ? `${IMAGE_BASE_URL}${PROFILE_SIZE}${movie.poster_path}`
                : no_image
            }
          /> */}
        </Link>
      ))}
    </Wrapper>
  );
};

export default CreditsGrid;
